import React from 'react';
import { Switch, Route } from 'react-router-dom';
import Header from './components/Header/Header';
import MainVideoPage from './components/MainVideoPage/MainVideoPage';
import Upload from './components/Upload/Upload';
import './App.css';

const API_KEY = '110f950a-c58f-42c6-969e-3e58a775af61';
const searchURL = search =>
`https://project-2-api.herokuapp.com/videos/search?api_key=${API_KEY}&q=${search}`

searchVideos = event => {
      event.preventDefault();
      const searchWords = event.target.search.value;
      event.target.search.value = searchWords; 
      axios.get(searchUrl(searchWords))
          .then(res => {
            this.setState({
              gifs: res.data.data
            })
          })
          .catch(err => console.log(err))
    };

function App() {
    return (
      <div className="App">       
      <Header searchVideos={this.searchVideos}/>
      <Switch>
          <Route path='/' exact component={MainVideoPage} />
          <Route path='/video/:id' component={MainVideoPage} />
          <Route path='/upload' component={Upload} />
      </Switch>
      </div>
    );
}

export default App;
