/*import Poster from '../../assets/images/video-list-0.jpg'*/
import HeroVideoControls from '../HeroVideoControls/HeroVideoControls'
import './hero-video.scss'

function HeroVideo(props) {
    console.log(props.heroVideoDetails[0]);
    const image = props.heroVideoDetails[0]
    return (       
            <div className="main__hero-video-container">
            {props.heroVideoDetails[0] && 
            <>
                <video poster=/*{Poster}*/{props.heroVideoDetails[0]} src="../../assets/video/BrainStation Sample Video.mp4" type="video/mp4" className="main__hero-video"/>
            <HeroVideoControls /> 
            </>}
            </div>
            
            )
}

export default HeroVideo;
