import React from 'react';
import axios from 'axios';
import Header from '../Header/Header';
import HeroVideo from '../HeroVideo/HeroVideo';
import HeroVideoDetails from '../HeroVideoDetails/HeroVideoDetails';
import NextVideoQueue from '../NextVideoQueue/NextVideoQueue';
import CommentsSection from '../CommentsSection/CommentsSection';
import Upload from '../Upload/Upload';
import { Route } from 'react-router-dom';

const API_KEY = '110f950a-c58f-42c6-969e-3e58a775af61';

class UpdatedPage extends React.Component {
  
  updateHero = (id) => {
    axios.get(`https://project-2-api.herokuapp.com/videos/${id}/?api_key=${API_KEY}`)
        .then(res => {
          console.log(res.data, res.data.comments)

          this.setState({
            heroVideoDetails: [res.data],
            commentsList: res.data.comments  
          });
        })
        .catch(err => console.log(err));
  };

    componentDidUpdate(prevProps, prevState) {
        if (prevProps.match.params.id !== this.props.match.params.id) {
          console.log("prevProps", prevProps, prevState);
          console.log("props", this.props);
        this.updateHero(this.props.match.params.id);
        }
      };

    render() {
        return (
            <>
                <Header />
                  <HeroVideo heroVideoDetails={this.props.heroVideoDetails}/>
                  <div className="section-container">
                      <section className="main-section">
                          <HeroVideoDetails heroVideoDetails={this.props.heroVideoDetails}/>
                          <CommentsSection heroVideoComments={this.props.commentsList}/>
                      </section>
                        <NextVideoQueue heroVideoDetails={this.props.heroVideoDetails} nextVideoList={this.props.nextVideoList} />
                  </div>
                  <Route path='/upload' component={Upload}/>
            </>
        )
    }
}

export default UpdatedPage;
