import React from 'react';
import { Switch, Route } from 'react-router-dom';
import MainVideoPage from './components/MainVideoPage/MainVideoPage';
import Upload from './components/Upload/Upload';
import './App.css';

class App extends React.Component {

  state = {
    heroVideoDetails: [],
    nextVideoList: []
  };

componentDidMount() {
  let URL1 = `https://project-2-api.herokuapp.com/videos/1af0jruup5gu?api_key=${API_KEY}`;
  let URL2 = `https://project-2-api.herokuapp.com/videos/?api_key=${API_KEY}`;

  const requestOne = axios.get(URL1);
  const requestTwo = axios.get(URL2);

  axios.all([requestOne, requestTwo])
  .then(
    axios.spread((...res) => { 
      this.setState({
        heroVideoDetails: [res[0].data],
        nextVideoList: res[1].data
      });
    })
    )
    .catch(err => {
  console.error(err);
});
};

updateHero = (id) => {
  axios.get(`https://project-2-api.herokuapp.com/videos/${id}/?api_key=${API_KEY}`)
  .then(res => {  
    this.setState({
      heroVideoDetails: [res.data], 
    });
  })
  .catch(err => console.log(err));
};

componentDidUpdate(prevProps) {
  if (prevProps.match.params.id !== this.props.match.params.id) {
    this.updateHero(this.props.match.params.id);
  };
};

  render() {
      return (
        <div className="App">       
        <Switch>
            <Route path='/' exact render={(props) => (<MainVideoPage {...props} />)}/>
            <Route path='/video/:id' render={(props) => (<MainVideoPage {...props} />)}/>
            <Route path='/upload' render={(props) => (<Upload {...props} />)}/>
        </Switch>
        </div>
      );
  }
}

export default App;
