import React from 'react';
import CommentsForm from '../CommentsForm/CommentsForm'
import CommentsBox from '../CommentsBox/CommentsBox'

function CommentsSection(props) {

        return (
            <section className="sub-section">
                <CommentsForm heroVideo={props.comments}/>
                {props.heroVideoComments.map(commentsInfo => 
                    <CommentsBox key={commentsInfo.id} name={commentsInfo.name} date={commentsInfo.timestamp.toLocaleDateString()} text={commentsInfo.comment}/>)}
            </section>
        );
};

export default CommentsSection;
