import React from 'react';
import axios from 'axios';
import uuid from "uuid";
import { Switch, Route } from 'react-router-dom';
import Header from '../Header/Header';
import HeroVideo from '../HeroVideo/HeroVideo';
import HeroVideoDetails from '../HeroVideoDetails/HeroVideoDetails';
import CommentsSection from '../CommentsSection/CommentsSection';
import NextVideoQueue from '../NextVideoQueue/NextVideoQueue';
import Upload from '../Upload/Upload';
import UploadImage from "../../assets/images/Upload-video-preview.jpg";

class MainVideoPage extends React.Component {

    state = {
        heroVideoDetails: [],
        nextVideoList: []
      };
    
    componentDidMount() {
        axios.get('/videos')
        .then(res => {
          console.log(res.data)
          this.setState({
              heroVideoDetails: [res.data[0]],
              nextVideoList: res.data.slice(-1)[0]
            });
        })
        .catch(err => console.log(err))
      };

    updateHero = (id) => {
      window.scrollTo(0, 0);
      let commentsForm = document.querySelector('.comments-section__form');
      commentsForm.reset();

      axios.get(`/videos/${id}`)
      .then(res => {  
        
        this.setState({
          heroVideoDetails: [res.data]
        })
      })
      .catch(err => console.log(err));
    };
  
    componentDidUpdate(prevProps) {

      if (prevProps.match.params.id !== this.props.match.params.id) {
        this.updateHero(this.props.match.params.id);
      };
    };

    uploadVideo = (e) => {
      e.preventDefault();
      let time = new Date().getTime();

      let generatedId = `${uuid.v4()}`

      let newNextVideo = {
          id: generatedId,
          title: e.target.title.value,
          channel: 'Some New Channel',
          image: UploadImage
      }
      
      let newVideo = {
          id: generatedId,
          title: e.target.title.value,
          channel: 'Some New Channel',
          image: UploadImage,
          description: e.target.description.value,
          views: 0,
          likes: 0,
          duration: "3:45",
          video: '../../assets/video/BrainStation Sample Video.mp4',
          timestamp: time,
          comments: [
              {
                  "name": "Micheal Lyons",
                  "comment": "They BLEW the ROOF off at their last show, once everyone started figuring out they were going. This is still simply the greatest opening of acconcert I have EVER witnessed.",
                  "id": "1ab6d9f6-da38-456e-9b09-ab0acd9ce818",
                  "likes": 0,
                  "timestamp": 1545162149000
              },
              {
                  "name": "Gary Wong",
                  "comment": "Every time I see him shred I feel so motivated to get off my couch and hop on my board. He’s so talented! I wish I can ride like him one day so I can really enjoy myself!",
                  "id": "cc6f173d-9e9d-4501-918d-bc11f15a8e14",
                  "likes": 0,
                  "timestamp": 1544595784046
              },
              {
                  "name": "Theodore Duncan",
                  "comment": "How can someone be so good!!! You can tell he lives for this and loves to do it every day. Everytime I see him I feel instantly happy! He’s definitely my favorite ever!",
                  "id": "993f950f-df99-48e7-bd1e-d95003cc98f1",
                  "likes": 0,
                  "timestamp": 1542262984046
              }
          ]         
      }

  axios.post('/videos', {newVideo, newNextVideo})
  .then (res => {
      console.log(res.data[0])

      // let heroVideoDetails = res.data;
      // let lastItem = heroVideoDetails[heroVideoDetails.length-2]
      // lastItem.push(newNextVideo)
      // heroVideoDetails.splice(9, 0, newVideo);
      
      this.setState({
          heroVideoDetails: res.data[0],
          nextVideoDetails: res.data.slice(-1)[0]
      })
    })
  }

    render() {
        return (
          <>
          {/* <Route exact path="/upload" render={() => <Upload/>} /> */}
            <Header />           
            <HeroVideo heroVideoDetails={this.state.heroVideoDetails} />
            <div className="section-container">
                <section className="main-section">
                    <HeroVideoDetails heroVideoDetails={this.state.heroVideoDetails} />
                    <CommentsSection commentsList={this.statethis.state.heroVideoDetails} />
                </section>
                  <NextVideoQueue heroVideoDetails={this.state.heroVideoDetails} nextVideoList={this.state.nextVideoList} />
            </div> 
          </>
        );
    };
};

export default MainVideoPage;
