import React from 'react';
import CommentsForm from '../CommentsForm/CommentsForm'
import CommentsBox from '../CommentsBox/CommentsBox'

function CommentsSection(props) {
    return (
        <section className="sub-section">
            <CommentsForm heroVideoDetails={props.heroVideoDetails.comments} id={props.heroVideoDetails.comments.id} />
            {props.heroVideoDetails.comments.map(commentsInfo => 
                <CommentsBox key={commentsInfo.id} name={commentsInfo.name} date={commentsInfo.timestamp} text={commentsInfo.comment} />)}
        </section>
    );
};

export default CommentsSection;
