import React from 'react';
import Header from '../Header/Header';
import HeroVideo from '../HeroVideo/HeroVideo';
import HeroVideoDetails from '../HeroVideoDetails/HeroVideoDetails';
import NextVideoQueue from '../NextVideoQueue/NextVideoQueue';
import CommentsSection from '../CommentsSection/CommentsSection';
import Upload from '../Upload/Upload';
import { Route } from 'react-router-dom';

class UpdatedPage extends React.Component {

    

    componentDidUpdate(prevProps, prevState) {
        if (prevProps.match.params.id !== this.props.match.params.id) {
          console.log("prevProps", prevProps, prevState);
          console.log("props", this.props);
        this.props.updateHero(this.props.match.params.id);
        }
      };

    render() {
        return (
            <>
                <Header />
                  <HeroVideo heroVideoDetails={this.props.state.heroVideoDetails}/>
                  <div className="section-container">
                      <section className="main-section">
                          <HeroVideoDetails heroVideoDetails={this.props.state.heroVideoDetails}/>
                          <CommentsSection heroVideoComments={this.props.state.commentsList}/>
                      </section>
                        <NextVideoQueue heroVideoDetails={this.props.state.heroVideoDetails} nextVideoList={this.props.state.nextVideoList} />
                  </div>
                  <Route path='/upload' component={Upload}/>
            </>
        )
    }
}

export default UpdatedPage;
