import React from 'react';
import axios from 'axios';
import { Switch, Route } from 'react-router-dom';
import Header from './components/Header/Header';
import HeroVideo from './components/HeroVideo/HeroVideo';
import HeroVideoDetails from './components/HeroVideoDetails/HeroVideoDetails';
import NextVideoQueue from './components/NextVideo/NextVideo';
import CommentsSection from './components/CommentsSection/CommentsSection';
import Upload from './components/Upload/Upload';
import './App.css';

let videosURL = 'https://project-2-api.herokuapp.com/videos?api_key=110f950a-c58f-42c6-969e-3e58a775af61';
let videosURL2 = 'https://project-2-api.herokuapp.com/videos/1af0jruup5gu/?api_key=110f950a-c58f-42c6-969e-3e58a775af61';

const getOne = axios.get(videosURL)
const getTwo = axios.get(videosURL2)

class App extends React.Component {
  state = {
    videoList: [],
    heroVideo: []
  }

  componentDidMount() {
    axios.all([getOne, getTwo])
    .then(axios.spread((...responses) => {
      this.setState({videoList: responses[0].data})
      this.setState({heroVideo: responses[1].data})
    }))


    // axios.get(videosURL)
    //     .then(res => {
    //       console.log(res.data)
    //       this.setState({
    //         videoList: res.data})
    //       })
    //     .catch(err => console.log(err));
  }

  render() {
    return (
        <Switch>
          <Route path='/' exact>
            <div className="App">   
                <section className="header">
                    <Header />
                </section>   
                <HeroVideo />
                <div className="section-container">
                    <section className="main-section">
                      <HeroVideoDetails />
                      <CommentsSection heroVideo={this.state.heroVideo}/>
                    </section>
                    <section className="side-section">
                      <NextVideoQueue videoList={this.state.videoList}/>
                    </section>
                </div>
            </div>
          </Route>
          <Route path='/upload' component={Upload}/>
        </Switch>
    );
  }
}

export default App;

