import React from 'react';
import axios from 'axios';
import ViewsIcon from '../../assets/icons/SVG/Icon-views.svg'
import LikesIcon from '../../assets/icons/SVG/Icon-likes.svg'
import './hero-video-details.scss';

let videosURL = 'https://project-2-api.herokuapp.com/videos/1af0jruup5gu/?api_key=110f950a-c58f-42c6-969e-3e58a775af61';

class HeroVideoDetails extends React.Component {
    
    // state = {
    //     currentVideoDetails: []
    // }
    
    // componentDidMount() {
    //     axios.get(videosURL)
    //     .then(res => {
    //         console.log(res.data)
    //         this.setState({
    //             currentVideoDetails: res.data})
    //         });
    //   }
      
      render() {   
          return (    
              <div className="main__video-details">
                {this.state.currentVideoDetails.map(currentVideoDetailsInfo => 
                <HeroVideoDisplay key={currentVideoDetailsInfo.id} title={currentVideoDetailsInfo.title} channel={currentVideoDetailsInfo.channel} date={currentVideoDetailsInfo.timestamp} views={currentVideoDetailsInfo.views} likes={currentVideoDetailsInfo.likes} description={currentVideoDetailsInfo.description} /> )}          
            </div>       
        );
    };
};

export default HeroVideoDetails;

function HeroVideoDisplay(props) {
    const { title, channel, date, views, likes, description } = props;
    return (
        <div className="main__video-details">
            <h1 className="main__video-details-title">{title}</h1>
                <div className="main__video-details-container">
                    <div className="main__video-details-channel-date">
                        <h2 className="main__video-details-channel">{channel}</h2>
                        <h3 className="main__video-details-date">{date}</h3>
                    </div>
                    <div className="main__video-details-views-likes">
                        <div className="main__video-details-views"><img src={ViewsIcon} alt="Views Logo" className="main__video-icon main__video-icon-views"/>{views}</div>
                        <div className="main__video-details-likes"><img src={LikesIcon} alt="Likes Logo" className="main__video-icon main__video-icon-likes"/>{likes}</div>    
                    </div>
                </div>
                <div className="divider main__video-details-divider"></div>
                <p className="main__video-details-description">{description}</p>
        </div>
    )
};
